import React, {isValidElement, useEffect, useState} from "react";
import { Link } from "react-router-dom";

function EventDetail({id}) {

    useEffect(() => {
        fetchEventId();
    }, []);

    const [items, setItems] = useState([]);

    const fetchEventId = async () => {
        const data = await fetch(`/events/${id}`);
        const items = await data.json();
        console.log(items);
        setItems(items);
    }

    return (
        <section class="text-gray-600 body-font overflow-hidden">
        <div class="container px-5 py-24 mx-auto">
            <div class="-my-8 divide-y-2 divide-gray-100  rounded">
                {
                    items.map(item => 
                    <div class="py-8 flex flex-wrap md:flex-nowrap my-10">
                        <div class="md:w-64 md:mb-0 mb-6 flex-shrink-0 flex flex-col">
                        <span class="font-semibold title-font text-gray-700">{item.category}</span>
                    
                        <span class="mt-1 text-gray-500 text-sm">{item.date}</span>
                        </div>
                        <div class="md:flex-grow">
                        <h2 class="text-2xl font-medium text-gray-900 title-font mb-2 text-left">{item.event}</h2>
                        <p class="text-left mb-3 text-red-500">&mdash; {item.author}</p>
                        <p class="leading-relaxed text-left">{item.description}</p>
                        </div>
                        
                    </div>
                    )
                }

            </div>
        </div>
        </section>
     
    );
}

export default EventDetail;
