import React, { useState } from "react";

function AddEvent() {
  const [data, setData] = useState("");

  const handleChange = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    const currentInputFieldData = {
       [name]: value
    }

    const updateData = {
       ...data,
       ...currentInputFieldData
    }
   
    // merge data object with the current input field data object
    setData(updateData);
  };

  const handleSubmit = (e) => {
    console.log(data);
    e.preventDefault();
  };

  return (
    <div className="my-5">
      <div className="container">
        <div className="row">
          <div className="col-7 mx-auto">
            <h1>This is the Contact Page</h1>
            <form onSubmit={handleSubmit}>
               <label htmlFor="studentId">Student Id</label>
              <input type="number" name="name" className="form-control" onChange={handleChange}/>

              <label htmlFor="name">Author</label>
              <input type="text" name="author" className="form-control" onChange={handleChange}/>

              <label htmlFor="name">Middle Name</label>
              <input type="text" name="middleName" className="form-control" onChange={handleChange}/>

              <label htmlFor="lastName">Last Name</label>
              <input type="text" name="lastName" className="form-control" onChange={handleChange}/>

              <label htmlFor="name">Date of Birth</label>
              <input type="date" name="dob" className="form-control" onChange={handleChange}/>

              <label htmlFor="name">Gender</label>
               <select name="gender" onChange={handleChange} className='form-control'>
                  <option value="Male">Male</option>
                  <option value="Femal">Female</option>
               </select>
              <label htmlFor="name">Is Entrolled</label>
               <select name="isEnrolled" onChange={handleChange} className="form-control">
                  <option className="form-control" value="Yes">Yes</option>
                  <option className="form-control"  value="No">No</option>
               </select>

              <input type="submit" className="btn btn-info btn-sm my-5" value="Submit"
              />
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AddEvent;
