import React, {isValidElement, useEffect, useState} from "react";
import { Link } from "react-router-dom";
import { useParams } from 'react-router-dom';

function EventDetail() {
    const {id} = useParams();

    useEffect(() => {
        fetchEventId();
    }, []);

    const [items, setItems] = useState([]);

    const fetchEventId = async () => {
        const data = await fetch(`/events/${id}`);
        const items = await data.json();
        console.log(items);
        setItems(items);
    }

    return (
        <section class="text-gray-600 body-font">
        {
        items.map((item, index) => 
            <div class="container px-5 py-24 mx-auto" key={index}>
                <div class="text-center mb-20">
                <h1 class="sm:text-3xl text-2xl font-medium title-font text-gray-900 mb-4">{item.event}</h1>
                <p class="text-base leading-relaxed xl:w-2/4 lg:w-3/4 mx-auto text-gray-500s">{item.description}</p>
                <div class="flex mt-6 justify-center">
                    <div class="w-16 h-1 rounded-full bg-red-500 inline-flex"></div>
                </div>
                </div>  

                <div class="lg:w-2/3 w-full mx-auto overflow-auto">
                <table class="table-auto w-full text-left whitespace-no-wrap">
                    <thead>
                    <tr>
                        <th class="px-4 py-3 title-font tracking-wider font-medium text-gray-900 text-sm bg-gray-100 rounded-tl rounded-bl">Full Name</th>
                        <th class="px-4 py-3 title-font tracking-wider font-medium text-gray-900 text-sm bg-gray-100">Email</th>
                        <th class="px-4 py-3 title-font tracking-wider font-medium text-gray-900 text-sm bg-gray-100">Telephone</th>
                        <th class="px-4 py-3 title-font tracking-wider font-medium text-gray-900 text-sm bg-gray-100">Date Registered</th>
                        <th class="w-10 title-font tracking-wider font-medium text-gray-900 text-sm bg-gray-100 rounded-tr rounded-br"></th>
                    </tr>
                    </thead>
                    <tbody>
                       <tr>
                            <td class="px-4 py-3">Start</td>
                            <td class="px-4 py-3">5 Mb/s</td>
                            <td class="px-4 py-3">15 GB</td>
                            <td class="px-4 py-3 text-lg text-gray-900">Free</td>
                        </tr>
                        
                    
                    </tbody>
                </table>
                </div>
                <div class="flex pl-4 mt-4 lg:w-2/3 w-full mx-auto">
                <a class="text-red-500 inline-flex items-center md:mb-2 lg:mb-0">Learn More
                    <svg fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" class="w-4 h-4 ml-2" viewBox="0 0 24 24">
                    <path d="M5 12h14M12 5l7 7-7 7"></path>
                    </svg>
                </a>
                <button class="flex ml-auto text-white bg-red-500 border-0 py-2 px-6 focus:outline-none hover:bg-red-600 rounded">Button</button>
                </div>

                <button class="flex mx-auto mt-16 text-white bg-red-500 border-0 py-2 px-8 focus:outline-none hover:bg-red-600 rounded text-lg">Button</button>
            </div>
        )
    }
        </section>     
    );
}

export default EventDetail;
