import React, { useState } from "react";

function AddEvent() {
  const [data, setData] = useState("");

  const handleChange = (e) => {
    const eventName = e.target.eventName;
    const value = e.target.value;
    const currentInputFieldData = {
       [eventName]: value
    }

    const updateData = {
       ...data,
       ...currentInputFieldData
    }
   
    // merge data object with the current input field data object
    setData(updateData);
  };

  const handleSubmit = (e) => {
    console.log(data);
    e.preventDefault();
  };

  return (
    <section class="text-gray-600 body-font relative">
    <div class="container px-5 py-24 mx-auto">
      <div class="flex flex-col text-center w-full mb-12">
        <h1 class="sm:text-3xl text-2xl font-medium title-font mb-4 text-gray-900">Add Event</h1>
        <p class="lg:w-2/3 mx-auto leading-relaxed text-base">Whatever cardigan tote bag tumblr hexagon brooklyn asymmetrical gentrify.</p>
      </div>
      <div class="lg:w-1/2 md:w-2/3 mx-auto">
        <div class="flex flex-wrap -m-2">
          <div class="p-2 w-full">
            <div class="relative">
              <label for="eventName" class="leading-7 text-sm text-gray-600 text-left">Event Name</label>
              <input type="text" id="name" name="eventName" class="w-full bg-gray-100 bg-opacity-50 rounded border border-gray-300 focus:border-indigo-500 focus:bg-white focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out" onChange={handleChange} />
            </div>
          </div>
          <div class="p-2 w-1/2">
            <div class="relative">
              <label for="venue" class="leading-7 text-sm text-gray-600">Venue</label>
              <input type="text" id="venue" name="venue" class="w-full bg-gray-100 bg-opacity-50 rounded border border-gray-300 focus:border-indigo-500 focus:bg-white focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out" onChange={handleChange} />
            </div>
          </div>
          <div class="p-2 w-1/2">
            <div class="relative">
              <label for="capacity" class="leading-7 text-sm text-gray-600">capacity</label>
              <input type="number" id="capacity" name="capacity" class="w-full bg-gray-100 bg-opacity-50 rounded border border-gray-300 focus:border-indigo-500 focus:bg-white focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out" onChange="{handleChange}" />
            </div>
          </div>
          <div class="p-2 w-full">
            <div class="relative">
              <label for="description" class="leading-7 text-sm text-gray-600">description</label>
              <textarea id="description" name="description" class="w-full bg-gray-100 bg-opacity-50 rounded border border-gray-300 focus:border-indigo-500 focus:bg-white focus:ring-2 focus:ring-indigo-200 h-32 text-base outline-none text-gray-700 py-1 px-3 resize-none leading-6 transition-colors duration-200 ease-in-out" onChange={handleChange}></textarea>
            </div>
          </div>
          <div class="p-2 w-full">
            <button class="flex mx-auto text-white bg-red-500 border-0 py-2 px-8 focus:outline-none hover:bg-red-600 rounded text-lg">Button</button>
          </div>
        </div>
      </div>
    </div>
  </section>
  );
}

export default AddEvent;
